- EncodingType.SW_AUDIO_CODEC,               // 纯音频软编
- EncodingType.HW_AUDIO_CODEC,               // 纯音频硬编
- EncodingType.SW_VIDEO_CODEC,               // 纯视频软编
- EncodingType.HW_VIDEO_CODEC;               // 纯视频硬编
